import { useState } from "react"
import InfoBlock from "./InfoBlock"

function Stats(){
    const data = [
        {value: "3 141", title: "Nouveaux cas J-3", mainColor: "darkGreen", secondaryColor: "lightGreen"},
        {value: "16%", title: "Tension réa", mainColor: "lightBlue", secondaryColor: "darkBlue"},
        {value: "767", title: "Patients en réa", mainColor: "lightPurple", secondaryColor: "darkPurple"},
        {value: "3 141", title: "Nouveaux cas J-3", mainColor: "darkGreen", secondaryColor: "lightGreen"},
        {value: "16%", title: "Tension réa", mainColor: "lightBlue", secondaryColor: "darkBlue"},
        {value: "767", title: "Patients en réa", mainColor: "lightPurple", secondaryColor: "darkPurple"}
    ]
    const [visibleData, setDataCount] = useState(data.slice(0, 3))
    return(
        <div className="stats">
            <div className="sectionHeader">
                <h1>Chiffres Clés Favoris</h1>
                <a href="">Infos</a>
            </div>
            <form action="">
                <input type="text" placeholder="Cliquez pour changer les chiffres"/>
            </form>
            <div className="allInfoBlocks">
                {
                    visibleData.map((curData) => {
                        console.log(curData)
                        return(
                            <InfoBlock key={curData.title} data={curData}></InfoBlock>
                        )
                    })
                }
            </div>
            <div className="showMoreInfo">
                <button onClick={() => {setDataCount(visibleData + 3)}}>
                    <img src="/Icons/Arrow/down.png" alt="down arrow" />
                </button>
            </div>
        </div>
    )
}

export default Stats
