import { useState } from "react"
import InfoBlock from "./InfoBlock"

function Stats(){
    const data = [
        {value: "3 141", title: "Nouveaux cas J-3", mainColor: "darkGreen", secondaryColor: "lightGreen"},
        {value: "16%", title: "Tension réa", mainColor: "lightSlateGrey", secondaryColor: "darkSlateGrey"},
        {value: "767", title: "Patients en réa", mainColor: "lightPurple", secondaryColor: "darkPurple"},
        {value: "17", title: "Nouveaux décès", mainColor: "darkBlue", secondaryColor: "lightBlue"},
        {value: "35,0", title: "Taux d'incidence", mainColor: "darkOrange", secondaryColor: "lightOrange"},
        {value: "0,82", title: "R effectif", mainColor: "darkPink", secondaryColor: "lightPink"}
    ]
    const [visibleDataCount, setDataCount] = useState(3)
    return(
        <div className="stats">
            <div className="sectionHeader">
                <h1>Chiffres Clés Favoris</h1>
                <a href="">Infos</a>
            </div>
            <form action="">
                <input className="searchInfo" type="text" style = {{backgroundImage:'/Icons/Input/pencil.png'}}/>
            </form>
            <div className="allInfoBlocks">
                {
                    data.slice(0, visibleDataCount).map((curData) => {
                        console.log(curData)
                        return(
                            <InfoBlock key={curData.title} data={curData}></InfoBlock>
                        )
                    })
                }
            </div>
            <div className="showMoreInfo">
                <button onClick={() => {visibleDataCount === 3 ? setDataCount(data.length): setDataCount(3)}}>
                    <img src="/Icons/Arrow/down.png" alt="down arrow" />
                </button>
            </div>
        </div>
    )
}

export default Stats
