function TypeSelector(props){
    return(
        <button className={`switchType ${props.curType.selected?'selected': ''}`} onClick={
        () => {
            let types = props.types
            let setTypes = props.setTypes
            setTypes(types.map(e => e.type === props.curType.type? e.selected = true: e.selected = false))
            setTypes(types.filter)
            console.log(types)
        }}>
            <p>{props.curType.type}</p>
        </button>
    )
}

export default TypeSelector
